        <section id="login" class="login display">
            <div class="container">
                <div class="row">
                    <div class="box-item col-md-4">
                        <div class="flip-box">
                            <div class="flip-box-front text-center"
                                style="background-image:url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1NqD_V9SlaQ4jyTC05wvfPye5mLZvUnZk2g&usqp=CAU')">
                                <div class="inner color-white">
                                    <i class="fas fa-chalkboard-teacher flip-box-img"></i>

                                    <h3 class="flip-box-header">TEACHER LOGIN </h3>


                                </div>
                            </div>
                            <div class="flip-box-back text-center"
                                style="background-image:url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1NqD_V9SlaQ4jyTC05wvfPye5mLZvUnZk2g&usqp=CAU')">
                                <div class="inner color-white">
                                    <form class="teacher-form">
                                    <h4  class="error_teacher"></h4>
                                        <div class="inputBox">
                                            <input type="text" name="teacher_username" id="teacher_username"
                                                autocomplete="off" required value="">
                                            <label>Username</label>
                                        </div>
                                        <div class="inputBox">
                                            <input type="password" name="teacher_password" id="teacher_password"
                                                autocomplete="off" required value="">
                                            <label>Password</label>
                                            <a class="forgot-pass" onclick="forgotpass('teacher');">forgot password ?</a><br/>
                                            
                                        </div>
                                        <button class="flip-box-button" onclick="login(event,'teacher');">LOGIN</button>
                                    </form>
                                    <form class="display teacher-forgotpass-form">
                                        <div class="inputBox">
                                            <input type="email" name="admim_email" id="admin_email"
                                                autocomplete="off" required value="">
                                            <label>E-mail</label>
                                        </div>
                                       
                                       
                                        <button class="flip-box-button" onclick="resetpass(event,'teacher');">Reset Password</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="box-item col-md-4">
                        <div class="flip-box">
                            <div class="flip-box-front text-center"
                                style="background-image: url('data:image/jpeg;base64,');">
